BEGIN;

DROP   TABLE IF     EXISTS subsystems;
DROP   TABLE IF     EXISTS devices;
DROP   TABLE IF     EXISTS vendors;

CREATE TABLE IF NOT EXISTS vendors(
	id integer PRIMARY KEY,	-- Hex digit in integer format (0x0e11 -> 3601)
	name varchar(128) NOT NULL CHECK (trim(both from name) NOT LIKE '')
);
CREATE TABLE IF NOT EXISTS devices(
	id integer PRIMARY KEY,	-- like a 'serial'
	vendor_id integer REFERENCES vendors(id),
	device_id integer,		-- Hex digit in integer format (0x8801 -> 34817)
	name varchar(128) NOT NULL CHECK (trim(both from name) NOT LIKE '')
);
CREATE TABLE IF NOT EXISTS subsystems(
	id integer PRIMARY KEY,
	device_id integer REFERENCES devices(id),
	subvendor_id integer,	-- Hex digit in integer format (0x0e11 -> 3601)
	subdevice_id integer,	-- Hex digit in integer format (0x4091 -> 16529)
	name varchar(128) NOT NULL CHECK (trim(both from name) NOT LIKE '')
);

